buf=$(cat input.txt); first=$(for i in $(seq 0 1 $((${#buf}-14))); do group="${buf:$i:14}"; echo -e "$(($i + 14)).: $(echo -e "$group" | fold -w1 | sort | uniq | tr -d '[:space:]' | awk '{ (length($1) == 14) ? isbeacon = "true"  : isbeacon = "false"; print(""), isbeacon }')"; done | rg 'true' | head -n 1); echo $first;
